import{openDB}from"idb";const initdb=async()=>openDB("jate",1,{upgrade(t){t.objectStoreNames.contains("jate")?console.log("jate database already exists"):(t.createObjectStore("jate",{keyPath:"id",autoIncrement:!0}),console.log("jate database created"))}});export const putDb=async t=>{console.log("PUT to the database");try{const e=(await openDB("jate",1)).transaction("jate","readwrite").objectStore("jate").put({id:1,content:t}),a=await e;console.log("Data saved to the database",a)}catch(t){console.error("Error saving data to the database",t)}};export const getDb=async()=>{console.log("GET all from the database");try{const t=(await openDB("jate",1)).transaction("jate","readonly").objectStore("jate").getAll(),e=await t;return console.log("Data retrieved from the database",e),e.length>0?e[0].content:null}catch(t){console.error("Error retrieving data from the database",t)}};initdb();